# - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -# Local functions# - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -downloadAny <- function(tdd, ...) {  downloadBroad <- function(tdd, pattern="[.]ismpolish[.]data[.]txt$", ...) {    downloadFiles(tdd, pattern=pattern, ...);  }    downloadHudsonAlpha <- function(tdd, pattern="[.]Paired_LogR[.]txt$", ...) {    downloadFiles(tdd, pattern=pattern, ...);  }  downloadHudsonAlpha <- function(tdd, pattern="[.]XandYintensity[.]txt$", ...) {    downloadFiles(tdd, pattern=pattern, ...);  }  downloadAgilent <- function(tdd, pattern="[.]data[.]txt$", ...) {    downloadFiles(tdd, pattern=pattern, ...);  }  path <- getUrlPath(tdd);  fullname <- basename(path);  site <- gsub("^([a-z.]*)_.*", "\\1", fullname);  if (site == "broad.mit.edu") {    fcn <- downloadBroad;  } else if (site == "hudsonalpha.org") {    fcn <- downloadHudsonAlpha;  } else if (site == "mskcc.org") {    fcn <- downloadAgilent;  } else if (site == "hms.harvard.edu") {    fcn <- downloadAgilent;  } else {    throw("Unknown site: ", site);  }  fcn(tdd, ...);} # downloadAny();############################################################################# HISTORY:# 2009-04-15# o Updated.# 2009-04-13# o Created.############################################################################